var app={};app.init=function(){app.smoothScroll(),app.stickyMenu(),app.homeBanner(),app.testimonials()},app.smoothScroll=function(){$("a.smooth").on("click",function(a){a.preventDefault();var t=$("header").height();$("html, body").animate({scrollTop:$($.attr(this,"href")).offset().top-t},500)})},app.stickyMenu=function(){var a=$("header").height();$(window).scroll(function(){$(window).scrollTop()>a?$("header").addClass("sticky"):$("header").removeClass("sticky")})},app.homeBanner=function(){$(".homeBanner").slick({dots:!0,nextArrow:'<button class="arrow-next"><i class="fa fa-angle-right"></i></button>',prevArrow:'<button class="arrow-prev"><i class="fa fa-angle-left"></i></button>',autoplay:!0,autoplaySpeed:5e3,fade:!0,cssEase:"ease"})},app.testimonials=function(){$(".testSlider").slick()},$(function(){app.init()});